mapper.select:
select p.permission from t_sys_user u, t_sys_role r, t_sys_permission p, t_sys_user_role ur, t_sys_role_permission rp where u.userid = r.roleid and u.userid = ur.userid and r.roleid = ur.roleid
and r.roleid = rp.roleid and p.perid = rp.perid
and u.username = #{username}

mapper.update:
update t_sys_user
set username = #{username,jdbcType=VARCHAR},
  PASSWORD = #{password,jdbcType=VARCHAR},
  salt = #{salt,jdbcType=VARCHAR},
  createdate = #{createdate,jdbcType=TIMESTAMP}
where userid = #{userid,jdbcType=INTEGER}
